$(document).ready(function() {
    // 
    function sign(url, method, data) {
        $.ajax({
            type: method,
            url: url,
            data: data,
            dataType: "json",
            success: function(data) {

                if (data.status == 'error') {
                    var title = "Thất Bại !";
                    var button = "Thử Lại";
                } else {
                    var title = "Thành Công";
                    var button = "Đóng";
                    setInterval(() => {
                        window.location.replace("/");
                    }, 2000);
                }

                Swal.fire({
                    title: title,
                    text: data.msg,
                    icon: data.status,
                    confirmButtonText: button
                });
            }
        });
    }
});